import React from "react"
import axios from 'axios';
import { Link, useParams } from 'react-router-dom';
var idno;


export default (props) =>
{
    var [userData,setStateuserData]=useState({
        id:'',
        name:'',
        email:'',
        phone:''

    });
    let {idno}=useParams();
    let { id, name, email, phone}= userData

    useEffrct(()=>
    {
        const url=`http://localhost:3002/users/${idno}`
        axios.get(url).then( (result)=> setStateuserData(result.data));
    },[]);
    

        return (<>
            
            <div className="container-fluid  p-5 bg-success">
                <h2>VIEW COMPONENT PAGE</h2>
                <p className="fs-5">
                    {idno} th Record data
                </p>
                <ul className="fs-4">
                    <li>{id}</li>
                    <li>{name}</li>
                    <li>{email}</li>
                    <li>{phone}</li>
                </ul>
                <Link className="btn btn-dark" to="/restapi">GO BACK</Link>
             </div> 
            </>)
   
}
